import React, { useState, useEffect, useRef } from "react"; // useState, useEffect, useRef 훅 임포트

import "./styles/chat.css"; // CSS 스타일 시트 import
// import inputFrame2 from "./input-frame-2.svg"; // inputFrame2 이미지 import (실제 파일명으로 수정 필요)

export const Chat = ({ navigateTo = () => {} }) => {
	// navigateTo 함수가 없을 경우를 대비한 기본값
	const handleNavigate = (view) => {
		navigateTo(view);
	};

	// 채팅 메시지들을 관리하는 상태
	const [messages, setMessages] = useState([
		{ id: 1, text: "안녕하세요! 무엇을 도와드릴까요?", sender: "bot" },
		{ id: 2, text: "안녕하세요, 질문이 있습니다.", sender: "user" },
	]);
	// 사용자가 입력 중인 메시지를 관리하는 상태
	const [inputValue, setInputValue] = useState("");
	const chatBodyRef = useRef(null); // 채팅창 스크롤을 위한 ref

	// 메시지가 추가될 때마다 채팅창을 맨 아래로 스크롤
	useEffect(() => {
		if (chatBodyRef.current) {
			chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
		}
	}, [messages]);

	// 메시지 전송 처리 함수
	const handleSendMessage = () => {
		if (inputValue.trim() === "") return; // 빈 메시지 전송 방지

		const newMessage = {
			id: messages.length + 1,
			text: inputValue,
			sender: "user", // 사용자가 보낸 메시지로 가정
		};
		setMessages([...messages, newMessage]);
		setInputValue(""); // 입력창 비우기

		// TODO: 여기에 나중에 챗봇 API 호출 및 응답 처리 로직 추가
		// 예: const botResponse = await callChatbotAPI(inputValue);
		//      setMessages(prevMessages => [...prevMessages, { id: prevMessages.length + 1, text: botResponse, sender: 'bot' }]);
	};

	// Enter 키로 메시지 전송
	const handleKeyPress = (event) => {
		if (event.key === "Enter") {
			handleSendMessage();
		}
	};

	return (
		<div className="chat">
			<div className="chat-header">{/* 헤더 내용 (필요하다면 추가) */}</div>

			<div className="chat-body" ref={chatBodyRef}>
				<div className="main">
					<div className="div">
						<div className="chat-help">
							<div className="chat-man-img">
								<div className="img">
									<div className="frame" />
								</div>
							</div>
							{messages.map((msg) => (
								<div
									key={msg.id}
									className={`chat-message ${
										msg.sender === "bot" ? "bot-message" : "user-message"
									}`}
								>
									{msg.sender === "bot" && (
										<div className="chat-man-img">
											<div className="img">
												{/* 봇 프로필 이미지 또는 아이콘 */}
												<div className="frame" />
											</div>
										</div>
									)}
									<div
										className={
											msg.sender === "bot"
												? "chat-help-mention-wrapper"
												: "p-wrapper"
										}
									>
										<div
											className={
												msg.sender === "bot" ? "chat-help-mention" : "p"
											}
										>
											<div
												className={
													msg.sender === "bot"
														? "text-wrapper-2" /* 실제로는 text-wrapper-2와 text-wrapper-3이 합쳐진 형태여야 함 */
														: "text-wrapper-4"
												}
											>
												{msg.text}
											</div>
										</div>
									</div>
									{msg.sender === "user" && (
										<div className="img-wrapper">
											{" "}
											{/* 사용자 프로필 이미지 또는 아이콘 공간 */}
											<div className="frame-wrapper">
												<div className="frame-2" />
											</div>
										</div>
									)}
								</div>
							))}
						</div>
					</div>
				</div>
			</div>

			{/* 메시지 입력창 */}
			<div className="chat-input">
				<div className="div-3">
					<div
						className="chat-plus-button"
						onClick={() => alert("플러스 버튼 클릭됨!")}
					>
						<div className="frame-3">
							<img className="frame-4" alt="Frame" src={frame} />
						</div>
					</div>
					<div className="chat-input-textbox">
						<input
							type="text"
							className="text-wrapper-5" // 실제로는 input 태그여야 함
							placeholder="메시지를 입력하세요..."
							value={inputValue}
							onChange={(e) => setInputValue(e.target.value)}
							onKeyPress={handleKeyPress}
						/>
					</div>
					<div className="input-button" onClick={handleSendMessage}>
						<div className="input">
							{/* <img className="input-frame" alt="Input" src={inputFrame2} /> */}{" "}
							{/* inputFrame2 사용하려면 import 필요 */}
							<div className="text-wrapper-6">보내기</div>{" "}
							{/* 임시 "보내기" 텍스트 */}
						</div>
					</div>
				</div>
			</div>

			{/* 하단 네비게이션 */}
			<div className="chat-navigation">
				<div className="group">
					<div className="group-2" onClick={() => handleNavigate("main")}>
						<img className="vector" alt="Vector" src={vector} />
						<div className="text-wrapper-6">Home</div>
					</div>
					<div className="group-3" onClick={() => handleNavigate("chat")}>
						<div className="overlap-group">
							<img className="vector-2" alt="Vector" src={image1} />
						</div>
						<div className="text-wrapper-7">Chat Bot</div>
					</div>
					<div className="group-4" onClick={() => handleNavigate("analysis")}>
						<img className="vector-3" alt="Vector" src={vector2} />
						<div className="text-wrapper-8">Analysis</div>
					</div>
				</div>
			</div>
		</div>
	);
};
