import React, { useState, useRef } from "react"; // useState, useRef 훅 임포트
import "./styles/main.css"; // CSS 스타일 임포트
export const Main = ({ navigateTo = () => {} }) => {
	const [selectedCategory, setSelectedCategory] = useState(null);
	const [selectedVideo, setSelectedVideo] = useState(null);
	const [videoThumbnail, setVideoThumbnail] = useState(null);
	const [isVideoUploaded, setIsVideoUploaded] = useState(false); // 비디오 업로드 상태

	// 파일 입력을 위한 ref
	const fileInputRef = useRef(null);

	const handleNavigate = (path) => {
		// 페이지 이동 시 업로드된 비디오 및 썸네일 상태 초기화 (선택 사항)
		// setSelectedVideo(null);
		// setVideoThumbnail(null);
		// setIsVideoUploaded(false);
		navigateTo(path);
	};

	const handleCategorySelect = (category) => {
		setSelectedCategory(category);
		console.log(`${category} 선택됨`);
	};

	// "업로드" 버튼 클릭 시 숨겨진 파일 입력 필드 클릭
	const triggerVideoUpload = () => {
		if (fileInputRef.current) {
			fileInputRef.current.click();
		}
	};

	// 파일 선택 시 호출되는 함수
	const handleVideoFileChange = (event) => {
		const file = event.target.files[0];
		if (file && file.type.startsWith("video/")) {
			setSelectedVideo(file);
			const thumbnailUrl = URL.createObjectURL(file);
			setVideoThumbnail(thumbnailUrl);
			setIsVideoUploaded(true);
			console.log("비디오 선택됨:", file.name);
			// 가이드 영역(view)을 가리기 위한 로직을 여기에 추가하거나,
			// 썸네일 표시 컴포넌트의 스타일을 조정하여 가이드 위에 오도록 합니다.
		} else {
			alert("비디오 파일을 선택해주세요.");
			setSelectedVideo(null);
			setVideoThumbnail(null);
			setIsVideoUploaded(false);
		}
	};

	const handleAnalyzeClick = () => {
		if (!selectedVideo) {
			alert("분석할 비디오를 먼저 업로드해주세요.");
			return;
		}
		if (!selectedCategory) {
			alert("운동 카테고리를 선택해주세요.");
			return;
		}
		console.log(
			`분석 페이지로 이동: 비디오 - ${selectedVideo.name}, 카테고리 - ${selectedCategory}`
		);
		// 실제 분석 페이지로 이동 시 비디오 파일과 선택된 카테고리 정보를 함께 전달해야 합니다.
		// navigateTo("analysis", { video: selectedVideo, category: selectedCategory });
		// 위와 같이 navigateTo 함수가 상태 전달을 지원한다고 가정합니다.
		// 또는 Context API, Redux 등을 사용하여 상태를 관리할 수 있습니다.
		navigateTo("analysis"); // 우선 페이지 이동만 처리
	};

	return (
		<div className="main">
			{/* 운동 카테고리 선택 */}
			<div className="chip-group">
				<div
					className={`chip ${selectedCategory === "플랭크" ? "selected" : ""}`}
					onClick={() => handleCategorySelect("플랭크")}
				>
					<div className="element">플랭크</div>
				</div>
				<div
					className={`chip ${selectedCategory === "런지" ? "selected" : ""}`}
					onClick={() => handleCategorySelect("런지")}
				>
					<div className="element">런지</div>
				</div>
				<div
					className={`chip ${selectedCategory === "푸쉬업" ? "selected" : ""}`}
					onClick={() => handleCategorySelect("푸쉬업")}
				>
					<div className="element">푸쉬업</div>
				</div>
				{/* 필요에 따라 다른 운동 카테고리 추가 */}
			</div>

			<div className="info">
				{selectedCategory
					? `선택된 운동: ${selectedCategory}`
					: "운동 카테고리를 선택하세요."}
			</div>

			{/* 가이드 표시 영역 / 비디오 썸네일 표시 영역 */}
			<div className="view">
				{!isVideoUploaded && (
					<div className="guide-text">
						{/* 여기에 운동 가이드 내용을 표시합니다. */}
						{selectedCategory
							? `${selectedCategory} 운동 가이드가 여기에 표시됩니다.`
							: "운동을 선택하면 가이드가 표시됩니다."}
					</div>
				)}
				{isVideoUploaded && videoThumbnail && (
					<div
						className="thumbnail-container"
						style={{ position: "relative", width: "100%", height: "100%" }}
					>
						<video
							src={videoThumbnail}
							alt="Video thumbnail"
							style={{ width: "100%", height: "100%", objectFit: "cover" }}
							controls={false} // 썸네일이므로 컨트롤 숨김
						/>
						{/* 썸네일 위에 '분석하기' 버튼을 위치시킬 수 있습니다. */}
						{/* 아니면 아래 'view-2' 영역과 별도로 '분석하기' 버튼을 조건부 렌더링합니다. */}
					</div>
				)}
			</div>

			{/* 실시간 분석 및 업로드 버튼 영역 */}
			<div className="view-2">
				<div
					className="camera"
					onClick={() => {
						if (!selectedCategory) {
							alert(
								"실시간 분석을 시작하기 전에 운동 카테고리를 선택해주세요."
							);
							return;
						}
						console.log(
							`실시간 분석 클릭됨 - 선택된 운동: ${selectedCategory}`
						);
						// navigateTo("realtime-analysis", { category: selectedCategory });
						// 실시간 분석 페이지로 이동하는 로직 (필요시)
					}}
				>
					<div className="text-wrapper-2">실시간 분석</div>
					<img className="vector-2" alt="Camera Icon" src={vector} />
				</div>

				{/* 비디오 업로드 관련 UI */}
				{!isVideoUploaded ? (
					<div className="upload" onClick={triggerVideoUpload}>
						<div className="upload-2">업로드</div>
						<img
							className="material-symbols-upload-rounded"
							alt="Upload Icon"
							src={materialSymbolsUploadRounded}
						/>
						<img
							className="rectangle-2"
							alt="Upload button background"
							src={rectangle2}
						/>
					</div>
				) : (
					<div className="analyze-action">
						<button className="analyze-button" onClick={handleAnalyzeClick}>
							분석하기
						</button>
						<button
							className="re-upload-button"
							onClick={() => {
								setIsVideoUploaded(false);
								setSelectedVideo(null);
								setVideoThumbnail(null);
								if (fileInputRef.current) {
									fileInputRef.current.value = ""; // 파일 입력 초기화
								}
								triggerVideoUpload(); // 다시 업로드 창 열기
							}}
						>
							다른 영상 업로드
						</button>
					</div>
				)}
				{/* 숨겨진 파일 입력 필드 */}
				<input
					type="file"
					ref={fileInputRef}
					style={{ display: "none" }}
					accept="video/*"
					onChange={handleVideoFileChange}
				/>
			</div>

			{/* 날짜 선택 입력 필드 */}
			<div className="input">
				<div
					className="div"
					onClick={() => alert("날짜 선택 기능이 구현될 예정입니다.")}
				>
					날짜 선택
				</div>
				<img className="vector-2-2" alt="Calendar Icon" src={vector} />
			</div>

			{/* 하단 네비게이션 바 */}
			<div className="home-navigation">
				<div className="overlap">
					<div className="chatbot" onClick={() => handleNavigate("chat")}>
						<img className="vector-3" alt="Chatbot Icon" src={image} />
						<div className="text-wrapper-4">Chat Bot</div>
					</div>
					<div className="analysis" onClick={() => handleNavigate("analysis")}>
						<img className="vector-4" alt="Analysis Icon" src={vector2} />
						<div className="text-wrapper-5">Analysis</div>
					</div>
					<div className="home" onClick={() => handleNavigate("main")}>
						{/* 현재 페이지가 Main이므로, navigateTo("main")을 호출하거나 아무것도 안 하도록 설정할 수 있습니다. */}
						{/* <div className="text-wrapper-6 active">Home</div> */}
						<div className="text-wrapper-6">Home</div>
					</div>
				</div>
			</div>
		</div>
	);
};
