import React, { useState } from "react";
//import "./App.css"; // App.css를 사용하려면 이 줄의 주석을 해제하세요.
// Main 컴포넌트를 가져옵니다. main.jsx 파일의 경로가 src/app/main.jsx라고 가정합니다.
import { Main } from "./app/main";
import { Chat } from "./app/chat";
import { Analysis } from "./app/analysis";
// import { evaluate } from "./app/evaluate"; // evaluate 컴포넌트는 현재 App.jsx에서 직접 사용되지 않음

function App() {
	// 현재 활성화된 뷰를 관리하는 상태 ('main', 'chat', 'analysis')
	// 기본값으로 'main' (홈 화면)을 설정합니다.
	const [currentView, setCurrentView] = useState("main");

	// 뷰를 변경하는 함수
	const navigateTo = (view) => {
		setCurrentView(view);
	};

	return (
		<div className="container">
			<h1>AI 홈트 모니터링</h1>

			{/* 네비게이션 탭 UI */}
			<nav className="navigation-tabs">
				<button
					onClick={() => navigateTo("main")}
					className={`nav-button ${currentView === "main" ? "active" : ""}`}
				>
					홈
				</button>
				<button
					onClick={() => navigateTo("chat")}
					className={`nav-button ${currentView === "chat" ? "active" : ""}`}
				>
					챗봇
				</button>
				<button
					onClick={() => navigateTo("analysis")}
					className={`nav-button ${currentView === "analysis" ? "active" : ""}`}
				>
					분석
				</button>
			</nav>

			{/* 현재 선택된 뷰에 따라 해당 컴포넌트를 렌더링 */}
			<main className="view-content">
				{currentView === "main" && <Main navigateTo={navigateTo} />}
				{currentView === "chat" && <Chat navigateTo={navigateTo} />}
				{currentView === "analysis" && <Analysis navigateTo={navigateTo} />}
			</main>

			{/*
				참고: main.jsx, chat.jsx, analysis.jsx 파일 내부에 자체적인 하단 네비게이션 UI가 있다면,
				App.jsx에서 구현한 이 상단 탭 네비게이션과 기능이 중복될 수 있습니다.
				애플리케이션 전체의 네비게이션 일관성을 위해,
				App.jsx의 네비게이션을 메인으로 사용한다면,
				각 페이지 컴포넌트 내부의 네비게이션은 제거하거나,
				App.jsx의 navigateTo 함수를 props로 전달받아 연동시키는 방식으로 수정하는 것을 고려해볼 수 있습니다.
			*/}
		</div>
	);
}

export default App;
