import React, { useState, useEffect, useRef } from "react";

// CSS 파일 import
import "./styles/analysis.css";

// 이미지 import (경로는 실제 파일 위치에 맞게 수정해주세요)
// CSS background-image로 사용되는 이미지들은 JSX에서 직접 import하지 않아도 됩니다.
// 하지만 <img src={...}> 태그에 사용되는 이미지들은 import 해야 합니다.
import analysisCopy2 from "./styles/asset/copy2.svg"; // 피드백 복사 아이콘
import analysisVideo from "./styles/asset/analysis_video.svg"; // 분석 비디오 썸네일/플레이스홀더
import analysisEvaluationButton from "./styles/asset/evaluation_button.svg"; // 평가 기준 아이콘

// 네비게이션 아이콘 (네비게이션 바를 제거했으므로 이 import들은 더 이상 필요 없습니다.)
// import vectorWrapper from "./styles/asset/vector-wrapper.svg";
// import vector4 from "./styles/asset/Vector-4.svg";
// import vector5 from "./styles/asset/Vector-5.svg";

// 상태 표시줄 아이콘 (상태 표시줄 아이콘을 제거했으므로 이 import들은 더 이상 필요 없습니다.)
// import analysisWifi from "./styles/asset/wifi.svg";
// import analysisCellular from "./styles/asset/cellular.svg";
// import analysisBattery from "./styles/asset/battery.svg";
// import analysisTime from "./styles/asset/time.svg";

export const Analysis = ({ navigateTo = () => {} }) => {
	// 평가 기준 모달 상태
	const [showEvaluationModal, setShowEvaluationModal] = useState(false);

	// 평가 기준 모달 토글 함수
	const toggleEvaluationModal = () => {
		setShowEvaluationModal(!showEvaluationModal);
	};

	// Yolo 모델로부터 받아올 데이터 상태
	const [exerciseData, setExerciseData] = useState({
		score: 0,
		time: 0, // 운동 시간 (초)
		count: 0, // 운동 횟수
		feedback: "",
	});

	// Yolo 모델로부터 데이터를 받아오는 함수 (가정)
	const fetchYoloData = async () => {
		try {
			// 실제 API 엔드포인트로 변경해야 합니다.
			const response = await fetch("/api/yolo/results");
			const data = await response.json();

			// 받아온 데이터로 상태 업데이트
			setExerciseData({
				score: data.score || 0,
				time: data.time || 0,
				count: data.count || 0,
				feedback: data.feedback || "아직 분석 결과가 없습니다.",
			});
		} catch (error) {
			console.error("Yolo 데이터 fetching 오류:", error);
			setExerciseData((prevData) => ({
				...prevData,
				feedback: "데이터를 불러오는 데 실패했습니다.",
			}));
		}
	};

	// 컴포넌트가 마운트될 때 Yolo 데이터 fetching 시작 (예시: 5초마다)
	useEffect(() => {
		// 실제로는 이벤트 기반 또는 필요에 따라 호출될 수 있습니다.
		const intervalId = setInterval(fetchYoloData, 5000);

		// 컴포넌트가 언마운트될 때 interval 정리
		return () => clearInterval(intervalId);
	}, []);

	// 운동 시간을 분:초 형식으로 변환하는 함수 (현재 사용되지 않음)
	const formatTime = (totalSeconds) => {
		const minutes = Math.floor(totalSeconds / 60);
		const seconds = totalSeconds % 60;
		return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
	};

	return (
		<div className="analysis">
			<div className="overlap-wrapper">
				{/* === 헤더 영역 시작 === */}
				{/* CSS의 .analysis-page-header 스타일이 적용될 컨테이너 */}
				<div className="analysis-page-header">
					{/* 로고가 필요하다면 여기에 <img className="logo" alt="Logo" src={yourLogoPath} /> 와 같이 추가 */}
					{/* CSS의 .analysis-page-header .header-title 스타일이 적용될 요소 */}
					<div className="header-title">HT GUARD</div>
				</div>
				{/* === 헤더 영역 끝 === */}

				{/* === 메인 콘텐츠 스크롤 영역 시작 === */}
				{/* CSS의 .analysis-content-area 스타일이 적용될 컨테이너 */}
				<div className="analysis-content-area">
					{/* 상태 표시줄 아이콘 (wifi, cellular 등)은 여기서 제거되었습니다. */}
					{/* "카테고리" 텍스트도 현재 JSX에 명확한 위치가 없으므로, 필요시 이 영역 내 적절한 곳에 배치합니다. */}

					{/* 비디오 섹션 */}
					{/* CSS의 .analysis-video 스타일이 적용될 요소 */}
					<img
						className="analysis-video"
						alt="Analysis video"
						src={analysisVideo} // 이미지 소스
					/>

					{/* 점수 및 운동 횟수 섹션 */}
					{/* CSS의 .score-and-times-container 스타일이 적용될 컨테이너 */}
					<div className="score-and-times-container">
						{/* 점수 표시 컨테이너 */}
						{/* CSS의 .analysis-score 스타일이 적용될 컨테이너 */}
						<div className="analysis-score">
							{/* 파란색 점수 배경 박스 (JSX의 클래스명 유지) */}
							{/* CSS의 .analysis-score .div 스타일이 적용될 요소 */}
							<div className="div">
								{/* 실제 점수 바 (너비는 JS로 동적 제어) */}
								{/* CSS의 .analysis-score .score 스타일이 적용될 요소 */}
								<div
									className="score"
									style={{
										width: `${exerciseData.score}%`,
										background:
											exerciseData.score > 70
												? "green"
												: exerciseData.score > 50
												? "yellow"
												: "red",
									}}
								/>
								{/* 점수 숫자 */}
								{/* CSS의 .analysis-score .text-wrapper-2 스타일이 적용될 요소 */}
								<div className="text-wrapper-2">{exerciseData.score}</div>
								{/* "점" 텍스트 */}
								{/* CSS의 .analysis-score .text-wrapper-3 스타일이 적용될 요소 */}
								<div className="text-wrapper-3">점</div>
							</div>
						</div>

						{/* 운동 횟수 텍스트 컨테이너 */}
						{/* CSS의 .analysis-times 스타일이 적용될 컨테이너 */}
						<div className="analysis-times">
							{/* 텍스트를 감싸는 테두리 박스 (JSX 클래스명 유지) */}
							{/* CSS의 .analysis-times .div-wrapper 스타일이 적용될 요소 */}
							<div className="div-wrapper">
								{/* "이번 운동으로 {count}회 했어요!" 텍스트 */}
								{/* CSS의 .analysis-times .text-wrapper-7 스타일이 적용될 요소 */}
								<div className="text-wrapper-7">
									이번 운동으로 {exerciseData.count}회 했어요!
								</div>
							</div>
						</div>
					</div>

					{/* 피드백 섹션: 텍스트와 복사 아이콘을 하나의 컨테이너로 묶습니다. */}
					{/* CSS의 .feedback-container 스타일이 적용될 컨테이너 */}
					<div className="feedback-container">
						{/* 피드백 텍스트 */}
						{/* CSS의 .feedback-container .feedback-text 스타일이 적용될 요소 */}
						<p className="feedback-text">{exerciseData.feedback}</p>
						{/* 복사 아이콘을 감싸는 버튼 역할의 div */}
						{/* CSS의 .analysis-copy-button 스타일이 적용될 컨테이너 */}
						<div
							className="analysis-copy-button"
							onClick={() =>
								navigator.clipboard.writeText(exerciseData.feedback)
							}
						>
							{" "}
							{/* 복사 기능 추가 예시 */}
							{/* 복사 아이콘 */}
							{/* CSS의 .analysis-copy-button .copy-icon 스타일이 적용될 요소 */}
							<img
								className="copy-icon"
								alt="Copy feedback"
								src={analysisCopy2}
							/>{" "}
							{/* 이미지 소스 */}
						</div>
					</div>

					{/* 평가기준 버튼 영역 */}
					{/* CSS의 .evaluation-button-area 스타일이 적용될 컨테이너 */}
					<div
						className="evaluation-button-area"
						onClick={toggleEvaluationModal}
						style={{
							cursor: "pointer",
							textAlign: "center",
							marginTop: "20px", // CSS로 이동 가능
						}}
					>
						{/* 평가 아이콘 */}
						{/* CSS의 .evaluation-button-area .evaluation-icon 스타일이 적용될 요소 */}
						<img
							className="evaluation-icon"
							alt="Evaluation Icon"
							src={analysisEvaluationButton} // 이미지 소스
							style={{ width: "50px", height: "auto" }} // CSS로 이동 권장
						/>
						{/* "평가기준" 텍스트 */}
						{/* CSS의 .evaluation-button-area .text-wrapper-8 스타일이 적용될 요소 */}
						<div
							className="text-wrapper-8" // JSX 클래스명 유지
							style={{ color: "#4caf50", fontWeight: "bold" }} // CSS로 이동 권장
						>
							평가기준
						</div>
					</div>

					{/* 평가 기준 모달 */}
					{showEvaluationModal && (
						<div
							className="evaluation-modal-overlay"
							onClick={toggleEvaluationModal}
						>
							{/* 모달 컨텐츠 클릭 시 이벤트 버블링 방지하여 모달이 닫히지 않도록 함 */}
							<div
								className="evaluation-modal-content"
								onClick={(e) => e.stopPropagation()}
							>
								<button
									className="close-modal-button"
									onClick={toggleEvaluationModal}
								>
									&times;
								</button>
								<h2>운동 평가 기준</h2>
								<img
									className="evaluation-modal-image"
									alt="Evaluation Criteria"
									src={
										analysisEvaluationButton
									} /* 상세 평가 기준 이미지 또는 내용 */
								/>
								{/* 여기에 추가적인 평가 기준 텍스트나 내용을 넣을 수 있습니다. */}
								<p>
									- 정확한 자세로 수행했는지
									<br />
									- 운동 범위가 적절했는지
									<br />
									- 설정된 시간/횟수를 잘 따랐는지
									<br />
									등을 종합적으로 평가합니다.
								</p>
							</div>
						</div>
					)}
				</div>
				{/* === 메인 콘텐츠 스크롤 영역 끝 === */}

				{/* 하단 네비게이션 바는 제거되었습니다. */}
			</div>
		</div>
	);
};
