import { useState, useEffect } from "react";
import { Camera, Upload, User, Activity, Settings, Clock } from "lucide-react";
import "./App.css";
export default function App() {
	const [activeTab, setActiveTab] = useState("main");
	const [workouts, setWorkouts] = useState([]);
	const [user, setUser] = useState(null);
	const [loading, setLoading] = useState(false);
	const [uploadStatus, setUploadStatus] = useState(null);

	useEffect(() => {
		// 페이지 로드 시 운동 목록과 사용자 정보 가져오기
		fetchWorkouts();
		fetchUserInfo();
	}, []);

	// Flask API 기본 URL
	const API_URL = "http://localhost:5000/api";

	const fetchWorkouts = async () => {
		try {
			// Flask API에서 운동 목록 가져오기
			const response = await fetch(`${API_URL}/workouts`);
			if (!response.ok) {
				throw new Error("서버에서 운동 목록을 가져오는데 실패했습니다");
			}
			const data = await response.json();
			setWorkouts(data);
		} catch (error) {
			console.error("운동 목록을 가져오는 중 오류 발생:", error);
			// API 연결 실패 시 대체 데이터 사용
			setWorkouts([
				{ id: 1, name: "플랭크", duration: 30, calories: 10 },
				{ id: 2, name: "팔굽혀펴기", duration: 60, calories: 15 },
				{ id: 3, name: "스쿼트", duration: 45, calories: 20 },
			]);
		}
	};

	const fetchUserInfo = async () => {
		try {
			// Flask API에서 사용자 정보 가져오기
			const response = await fetch(`${API_URL}/user`);
			if (!response.ok) {
				throw new Error("서버에서 사용자 정보를 가져오는데 실패했습니다");
			}
			const data = await response.json();
			setUser(data);
		} catch (error) {
			console.error("사용자 정보를 가져오는 중 오류 발생:", error);
			// API 연결 실패 시 대체 데이터 사용
			setUser({
				name: "사용자",
				level: "초보자",
				total_calories: 350,
				total_workouts: 15,
			});
		}
	};

	const uploadVideo = async (event) => {
		try {
			setLoading(true);
			setUploadStatus("업로드 중...");

			// 실제 구현에서는 파일을 FormData로 전송합니다
			// 이 예제에서는 파일 선택 UI가 없어서 더미 데이터를 전송합니다
			const formData = new FormData();
			// formData.append('video', event.target.files[0]);  // 실제 파일 업로드 시 사용

			// Flask API에 비디오 업로드
			const response = await fetch(`${API_URL}/upload_video`, {
				method: "POST",
				body: formData, // 실제 FormData 사용 시
				// headers: { 'Content-Type': 'application/json' }, // FormData 사용 시 불필요
				// body: JSON.stringify({ dummy: 'data' }), // 테스트용 더미 데이터
			});

			if (!response.ok) {
				throw new Error("비디오 업로드에 실패했습니다");
			}

			const result = await response.json();
			setUploadStatus(result.message);
		} catch (error) {
			console.error("비디오 업로드 중 오류 발생:", error);
			setUploadStatus("업로드 실패: " + error.message);
		} finally {
			setLoading(false);
		}
	};

	const startWorkout = async (workoutId) => {
		try {
			// Flask API에 운동 시작 알림
			const response = await fetch(`${API_URL}/start_workout`, {
				method: "POST",
				headers: { "Content-Type": "application/json" },
				body: JSON.stringify({ workout_id: workoutId }),
			});

			if (!response.ok) {
				throw new Error("운동 시작에 실패했습니다");
			}

			const result = await response.json();
			console.log(result.message);
			// 여기에 운동 시작 후 UI 업데이트 로직 추가
		} catch (error) {
			console.error("운동 시작 중 오류 발생:", error);
		}
	};

	return (
		<div className="flex flex-col h-screen bg-gray-100">
			{/* 헤더 */}
			<header className="bg-blue-600 text-white p-4">
				<h1 className="text-xl font-bold">AI 운동 모니터링</h1>
			</header>

			{/* 메인 콘텐츠 */}
			<main className="flex-1 p-4 overflow-auto">
				{activeTab === "main" && (
					<div className="grid grid-cols-1 md:grid-cols-3 gap-4">
						{/* AI GUARD 가이드 */}
						<div className="bg-white p-4 rounded-lg shadow">
							<h2 className="text-lg font-semibold mb-2">AI GUARD 가이드</h2>
							<p className="text-sm text-gray-600 mb-4">
								운동 자세와 움직임을 분석합니다
							</p>
							<button
								className="bg-blue-500 text-white p-2 rounded w-full"
								onClick={() => {}}
							>
								가이드 버튼
							</button>
						</div>

						{/* 화상 */}
						<div className="bg-white p-4 rounded-lg shadow md:col-span-2 border-2 border-dashed border-gray-300 flex flex-col items-center justify-center">
							<h2 className="text-lg font-semibold mb-4">화상</h2>
							{uploadStatus ? (
								<div className="text-center">
									<p className="text-green-500">{uploadStatus}</p>
								</div>
							) : (
								<div className="text-center">
									<Camera size={48} className="mx-auto text-gray-400 mb-2" />
									<p className="mb-4">
										비디오를 업로드하여 운동 분석을 시작하세요
									</p>
									<button
										className="bg-blue-500 text-white p-2 rounded"
										onClick={uploadVideo}
										disabled={loading}
									>
										{loading ? "처리중..." : "비디오 업로드"}
									</button>
								</div>
							)}
						</div>

						{/* 설정 화면 버튼 */}
						<div className="bg-white p-4 rounded-lg shadow">
							<h2 className="text-lg font-semibold mb-2">설정 화면</h2>
							<div className="flex flex-col space-y-2">
								<button
									className="bg-gray-200 hover:bg-gray-300 p-2 rounded text-left"
									onClick={() => setActiveTab("settings")}
								>
									설정 보기
								</button>
							</div>
						</div>
					</div>
				)}

				{activeTab === "settings" && (
					<div className="grid grid-cols-1 md:grid-cols-3 gap-4">
						{/* 왼쪽 패널 */}
						<div className="bg-white p-4 rounded-lg shadow">
							<h2 className="text-lg font-semibold mb-4">환경 설정</h2>
							<div className="mb-4">
								<h3 className="font-medium mb-2">화면 설정</h3>
								<div className="flex items-center">
									<input type="checkbox" id="darkMode" className="mr-2" />
									<label htmlFor="darkMode">다크 모드</label>
								</div>
							</div>
							<button
								className="bg-blue-500 text-white p-2 rounded w-full"
								onClick={() => setActiveTab("main")}
							>
								메인으로 돌아가기
							</button>
						</div>

						{/* 중앙 패널 */}
						<div className="bg-white p-4 rounded-lg shadow">
							<h2 className="text-lg font-semibold mb-2">제한 시간</h2>
							<div className="flex items-center justify-center h-64">
								<Clock size={64} className="text-blue-500" />
							</div>
							<div>
								<h3 className="font-medium mb-2">알림 설정</h3>
								<input type="range" className="w-full" />
							</div>
						</div>

						{/* 오른쪽 패널 */}
						<div className="flex flex-col gap-4">
							<div className="bg-white p-4 rounded-lg shadow">
								<h2 className="text-lg font-semibold mb-2">알림</h2>
								<p className="text-sm text-gray-600">운동 완료 시 알림 설정</p>
							</div>
							<div className="bg-white p-4 rounded-lg shadow">
								<h2 className="text-lg font-semibold mb-2">개인정보</h2>
								<p className="text-sm text-gray-600">개인정보 보호 설정</p>
							</div>
						</div>
					</div>
				)}
			</main>

			{/* 하단 탭 네비게이션 */}
			<nav className="bg-white border-t border-gray-200 p-2">
				<ul className="flex justify-around">
					<li
						className={`flex flex-col items-center p-2 ${
							activeTab === "main" ? "text-blue-500" : "text-gray-500"
						}`}
						onClick={() => setActiveTab("main")}
					>
						<Activity size={24} />
						<span className="text-xs mt-1">홈</span>
					</li>
					<li className="flex flex-col items-center p-2 text-gray-500">
						<Camera size={24} />
						<span className="text-xs mt-1">카메라</span>
					</li>
					<li className="flex flex-col items-center p-2 text-gray-500">
						<Upload size={24} />
						<span className="text-xs mt-1">업로드</span>
					</li>
					<li className="flex flex-col items-center p-2 text-gray-500">
						<Settings size={24} />
						<span className="text-xs mt-1">설정</span>
					</li>
					<li className="flex flex-col items-center p-2 text-gray-500">
						<User size={24} />
						<span className="text-xs mt-1">프로필</span>
					</li>
				</ul>
			</nav>
		</div>
	);
}
