import React, { useState } from "react";
import "./App.css";
// Main 컴포넌트를 가져옵니다. main.jsx 파일의 경로가 src/app/main.jsx라고 가정합니다.
import { Main } from "./app/main"; // Main 컴포넌트의 실제 경로로 수정해주세요.

// ChatPage 컴포넌트 (플레이스홀더)
// 실제 Chat 기능을 구현할 때 이 컴포넌트를 확장합니다.
const ChatPage = () => {
	return (
		<div>
			<h2>챗봇 페이지</h2>
			<p>챗봇 기능이 여기에 표시됩니다.</p>
		</div>
	);
};

// AnalysisPage 컴포넌트
// 기존 App.jsx의 영상 업로드 및 분석 결과 표시 로직을 이 컴포넌트로 옮깁니다.
const AnalysisPage = () => {
	const [video, setVideo] = useState(null);
	const [videoUploaded, setVideoUploaded] = useState(false);
	const [result, setResult] = useState(null);

	const handleUpload = async () => {
		const formData = new FormData();
		formData.append("video", video);

		// API 엔드포인트는 실제 환경에 맞게 수정해야 할 수 있습니다.
		const response = await fetch("http://localhost:5000/upload", {
			method: "POST",
			body: formData,
		});

		const data = await response.json();
		if (data.video_path) {
			setVideoUploaded(true);
			analyzeVideo(data.video_path); // 업로드 성공 후 바로 분석 시작
		}
	};

	const analyzeVideo = async (videoPath) => {
		// API 엔드포인트는 실제 환경에 맞게 수정해야 할 수 있습니다.
		const response = await fetch("http://localhost:5000/analyze", {
			method: "POST",
			headers: { "Content-Type": "application/json" },
			body: JSON.stringify({ video_path: videoPath }),
		});

		const data = await response.json();
		setResult(data);
	};

	return (
		<div>
			<h2>운동 영상 분석</h2>
			{!videoUploaded ? (
				<div className="upload-section">
					<input
						type="file"
						accept="video/*"
						onChange={(e) => setVideo(e.target.files[0])}
					/>
					<button onClick={handleUpload} disabled={!video}>
						영상 업로드 및 분석 시작
					</button>
					<p>MP4, MOV, AVI 형식을 지원합니다. 최대 100MB</p>
				</div>
			) : (
				<div className="result-section">
					<h3>분석 결과</h3>
					{result ? (
						<>
							<p>
								<strong>점수:</strong> {result.score}
							</p>
							<p>
								<strong>강점:</strong> {result.strengths.join(", ")}
							</p>
							<p>
								<strong>개선점:</strong> {result.improvements.join(", ")}
							</p>
						</>
					) : (
						<p>분석 중입니다...</p>
					)}
				</div>
			)}
		</div>
	);
};

function App() {
	// 현재 활성화된 뷰를 관리하는 상태 ('main', 'chat', 'analysis')
	// 기본값으로 'main' (홈 화면)을 설정합니다.
	const [currentView, setCurrentView] = useState("main");

	// 뷰를 변경하는 함수
	const navigateTo = (view) => {
		setCurrentView(view);
	};

	return (
		<div className="container">
			<h1>AI 홈트 모니터링</h1>

			{/* 네비게이션 탭 UI */}
			{/* App.css 파일에 .navigation-tabs, .nav-button, .nav-button.active 등의 스타일을 추가하여 꾸밀 수 있습니다. */}
			<nav className="navigation-tabs">
				<button
					onClick={() => navigateTo("main")}
					className={`nav-button ${currentView === "main" ? "active" : ""}`}
				>
					홈
				</button>
				<button
					onClick={() => navigateTo("chat")}
					className={`nav-button ${currentView === "chat" ? "active" : ""}`}
				>
					챗봇
				</button>
				<button
					onClick={() => navigateTo("analysis")}
					className={`nav-button ${currentView === "analysis" ? "active" : ""}`}
				>
					분석
				</button>
			</nav>

			{/* 현재 선택된 뷰에 따라 해당 컴포넌트를 렌더링 */}
			<main className="view-content">
				{currentView === "main" && <Main navigateTo={navigateTo} />}{" "}
				{/* navigateTo 함수를 Main 컴포넌트에 prop으로 전달 */}
				{currentView === "chat" && <ChatPage />}
				{currentView === "analysis" && <AnalysisPage />}
			</main>

			{/*
				참고: main.jsx 파일 내의 <div className="home-navigation"> 부분은
				App.jsx에서 구현한 이 네비게이션과 기능이 중복될 수 있습니다.
				만약 App.jsx의 네비게이션을 메인으로 사용한다면,
				main.jsx 내부의 네비게이션은 제거하거나, App.jsx의 navigateTo 함수를 props로 전달받아
				연동시키는 방식으로 수정하는 것을 고려해볼 수 있습니다.
			*/}
		</div>
	);
}

export default App;
