import React, { useState } from "react";
import "./App.css";
// Main 컴포넌트를 가져옵니다. main.jsx 파일의 경로가 src/app/main.jsx라고 가정합니다.
import { Main } from "./app/main"; // Main 컴포넌트의 실제 경로로 수정해주세요.
// import { Chat } from "./app/chat"; // Chat 컴포넌트 사용 시 주석 해제
// import { Analysis } from "./app/analysis"; // Analysis 컴포넌트 사용 시 주석 해제
// import { evaluate } from "./app/evaluate"; // evaluate 컴포넌트는 현재 사용되지 않음

// ChatPage 컴포넌트 (플레이스홀더)
// 실제 Chat 기능을 구현할 때 이 컴포넌트를 확장하거나 ./app/chat.jsx의 Chat 컴포넌트를 사용합니다.
const ChatPage = () => {
	return (
		<div>
			<h2>챗봇 페이지</h2>
			{/* TODO: './app/chat.jsx'의 Chat 컴포넌트 사용 고려 */}
			<p>챗봇 기능이 여기에 표시됩니다.</p>
		</div>
	);
};

// AnalysisPage 컴포넌트
// 영상 업로드 및 분석 결과 표시 로직을 담당합니다.
const AnalysisPage = () => {
	const [video, setVideo] = useState(null);
	const [videoUploaded, setVideoUploaded] = useState(false);
	const [result, setResult] = useState(null);
	const [isLoading, setIsLoading] = useState(false); // 로딩 상태 추가
	const [error, setError] = useState(null); // 에러 상태 추가

	// API 엔드포인트 (환경 변수 등으로 관리하는 것을 권장)
	const API_BASE_URL = "http://localhost:5000";

	const handleUpload = async () => {
		if (!video) return;
		setIsLoading(true);
		setError(null);
		setResult(null); // 이전 결과 초기화
		setVideoUploaded(false); // 업로드 상태 초기화

		const formData = new FormData();
		formData.append("video", video);

		try {
			const response = await fetch(`${API_BASE_URL}/upload`, {
				method: "POST",
				body: formData,
			});

			if (!response.ok) {
				const errorData = await response
					.json()
					.catch(() => ({ message: "서버 응답을 파싱할 수 없습니다." }));
				throw new Error(
					errorData.message || `HTTP error! status: ${response.status}`
				);
			}

			const data = await response.json();
			if (data.video_path) {
				setVideoUploaded(true); // 업로드 성공 표시
				// 업로드 성공 후 바로 분석 시작 (analyzeVideo 내부에서 로딩 상태 관리)
				await analyzeVideo(data.video_path);
			} else {
				throw new Error("서버로부터 비디오 경로를 받지 못했습니다.");
			}
		} catch (e) {
			console.error("Upload failed:", e);
			setError(`업로드 실패: ${e.message}`);
			setVideoUploaded(false);
		} finally {
			// analyzeVideo가 자체적으로 setIsLoading(false)를 호출하므로,
			// 여기서는 analyzeVideo가 호출되지 않은 경우(예: video_path가 없는 경우)에만 false로 설정
			if (!videoUploaded) {
				setIsLoading(false);
			}
		}
	};

	const analyzeVideo = async (videoPath) => {
		// analyzeVideo 호출 시 이미 setIsLoading(true)가 handleUpload에서 설정되었거나,
		// 별도 호출 시 여기서 설정 필요
		if (!isLoading) setIsLoading(true);
		setError(null); // 이전 에러 초기화
		setResult(null); // 이전 결과 초기화

		try {
			const response = await fetch(`${API_BASE_URL}/analyze`, {
				method: "POST",
				headers: { "Content-Type": "application/json" },
				body: JSON.stringify({ video_path: videoPath }),
			});

			if (!response.ok) {
				const errorData = await response
					.json()
					.catch(() => ({ message: "서버 응답을 파싱할 수 없습니다." }));
				throw new Error(
					errorData.message || `HTTP error! status: ${response.status}`
				);
			}
			const data = await response.json();
			setResult(data);
		} catch (e) {
			console.error("Analysis failed:", e);
			setError(`분석 실패: ${e.message}`);
		} finally {
			setIsLoading(false); // 분석 완료 또는 실패 시 로딩 해제
		}
	};

	return (
		<div>
			<h2>운동 영상 분석</h2>
			{/* 파일 업로드 섹션은 비디오가 업로드되지 않았거나, 업로드 후 분석 결과가 아직 없을 때 표시 */}
			{(!videoUploaded || !result) && !isLoading && (
				<div className="upload-section">
					<input
						type="file"
						accept="video/*"
						onChange={(e) => {
							setVideo(e.target.files[0]);
							setVideoUploaded(false); // 새 파일 선택 시 업로드 상태 초기화
							setResult(null); // 새 파일 선택 시 결과 초기화
							setError(null); // 새 파일 선택 시 에러 초기화
						}}
					/>
					<button onClick={handleUpload} disabled={!video || isLoading}>
						{isLoading ? "처리 중..." : "영상 업로드 및 분석 시작"}
					</button>
					<p>MP4, MOV, AVI 형식을 지원합니다. 최대 100MB</p>
				</div>
			)}

			{isLoading && <p>분석 중입니다...</p>}
			{error && <p style={{ color: "red" }}>오류: {error}</p>}

			{/* 분석 결과 섹션 */}
			{videoUploaded && !isLoading && !error && result && (
				<div className="result-section">
					<h3>분석 결과</h3>
					<p>
						<strong>점수:</strong> {result.score}
					</p>
					<p>
						<strong>강점:</strong>{" "}
						{result.strengths && result.strengths.join(", ")}
					</p>
					<p>
						<strong>개선점:</strong>{" "}
						{result.improvements && result.improvements.join(", ")}
					</p>
					{/* 다른 영상 분석 버튼 */}
					<button
						onClick={() => {
							setVideo(null);
							setVideoUploaded(false);
							setResult(null);
							setError(null);
							// 파일 입력 필드 초기화 (필요시)
							// const fileInput = document.querySelector('input[type="file"]');
							// if (fileInput) fileInput.value = "";
						}}
					>
						다른 영상 분석하기
					</button>
				</div>
			)}
		</div>
	);
};

function App() {
	// 현재 활성화된 뷰를 관리하는 상태 ('main', 'chat', 'analysis')
	// 기본값으로 'main' (홈 화면)을 설정합니다.
	const [currentView, setCurrentView] = useState("main");

	// 뷰를 변경하는 함수
	const navigateTo = (view) => {
		setCurrentView(view);
	};

	return (
		<div className="container">
			<h1>AI 홈트 모니터링</h1>

			{/* 네비게이션 탭 UI */}
			<nav className="navigation-tabs">
				<button
					onClick={() => navigateTo("main")}
					className={`nav-button ${currentView === "main" ? "active" : ""}`}
				>
					홈
				</button>
				<button
					onClick={() => navigateTo("chat")}
					className={`nav-button ${currentView === "chat" ? "active" : ""}`}
				>
					챗봇
				</button>
				<button
					onClick={() => navigateTo("analysis")}
					className={`nav-button ${currentView === "analysis" ? "active" : ""}`}
				>
					분석
				</button>
			</nav>

			{/* 현재 선택된 뷰에 따라 해당 컴포넌트를 렌더링 */}
			<main className="view-content">
				{currentView === "main" && <Main navigateTo={navigateTo} />}{" "}
				{currentView === "chat" && <ChatPage />}
				{/* TODO: './app/analysis.jsx'의 Analysis 컴포넌트 사용 또는 AnalysisPage 와의 역할 분담/통합 고려 */}
				{currentView === "analysis" && <AnalysisPage />}
			</main>

			{/*
				참고: main.jsx 파일 내의 <div className="home-navigation"> 부분은
				App.jsx에서 구현한 이 네비게이션과 기능이 중복될 수 있습니다.
				만약 App.jsx의 네비게이션을 메인으로 사용한다면,
				main.jsx 내부의 네비게이션은 제거하거나, App.jsx의 navigateTo 함수를 props로 전달받아
				연동시키는 방식으로 수정하는 것을 고려해볼 수 있습니다.
			*/}
		</div>
	);
}

export default App;
